use.library <- function(package) {
    if(!require(package, character.only=TRUE)) {
        install.packages(package, repos='http://cran.us.r-project.org')
    }
    library(package, character.only=TRUE)
}

transform <- function(dataframe) {

    # http://www.kdnuggets.com/2015/04/top-10-r-packages-kaggle.html
    top_kaggle_packages <- c("gbm", "randomForest", "e1071", "glmnet", "tau",
        "Matrix", "SOAR", "foreach", "doMC", "data.table")

    # http://www.kdnuggets.com/2015/06/top-20-r-machine-learning-packages.html
    # "RWeka"
    top_machine_learning_packages <- 
        c("e1071", "rpart", "nnet", "randomForest", 
          "kernlab", "glmnet", "ROCR", "gbm", "party", "tree", 
          "ipred", "lars", "earth")

    all_packages <- c(top_kaggle_packages, top_machine_learning_packages)

    for (package in all_packages) {
        use.library(package)
    }
    return(withColumn(dataframe, "new_column", lit(sigmoid(0.2))))
}
